import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useNavigationType, useLocation } from 'react-router-dom';
import { Provider } from 'react-redux';

import logo from './logo.svg';
import './App.css';

// Importaciones de los componentes de las páginas
import Login from './Pages/Login/Login';
import Registro from './Pages/Registro/Registro';
import { Registro2 } from './Pages/Registro-login/Registro2.jsx';
import { Login2 } from './Pages/Registro-login/Login2.jsx';
import About from "./Pages/Inicio/Inicio.jsx";
import Inicio from "./Pages/Inicio";
import Frame from './components/Shared/Footer';

// Store de Redux
import { store } from './Pages/store/store.js';

function App() {
  // Lógica para manejar el scroll y el título de la página
  const action = useNavigationType();
  const location = useLocation();
  const pathname = location.pathname;

  useEffect(() => {
    if (action !== "POP") {
      window.scrollTo(0, 0);
    }
  }, [action, pathname]);

  useEffect(() => {
    let title = "";
    let metaDescription = "";

    // Aquí puedes agregar los títulos y descripciones para otras rutas
    switch (pathname) {
      case "/":
        title = "Inicio";
        metaDescription = "Página de inicio";
        break;
      // otros casos...
    }

    if (title) {
      document.title = title;
    }

    if (metaDescription) {
      const metaDescriptionTag = document.querySelector(
        'head > meta[name="description"]'
      );
      if (metaDescriptionTag) {
        metaDescriptionTag.content = metaDescription;
      }
    }
  }, [pathname]);

  return (
    <Provider store={store}>
      <Router>
        <Routes>
          {/* Rutas del primer App.js */}
          <Route path="/login" element={<Login />} />
          <Route path="/registro" element={<Registro />} />
          <Route path="/registro2" element={<Registro2 />} />
          <Route path="/login2" element={<Login2 />} />
          <Route path="/inicio" element={<About />} />
          {/* Rutas del segundo App.js */}
          <Route path="/" element={<Inicio />} />
          {/* Puedes agregar o modificar rutas según sea necesario */}
          <Route path="*" element={<Navigate to="/inicio" replace />} />
        </Routes>
      </Router>
    </Provider>
  );
}

export default App;
